import { Component } from "react";


class EventPractice3 extends Component{
    state = {
        message: ''
    }


    render(){
        return(
            <div>
                <h1>이벤트 연습</h1>
            <input  type = "text" name = "message" placeholder="아무거나 쓰세요"
                onChange={
                    (e) => {
                        this.setState({
                            message: e.target.value
                        })
                    }
                }
            />
            <button onClick={() => {alert(this.state.message);this.setState({message:''})
            
        }

        } >확인</button>

            </div>

        )
    }
}

export default EventPractice3
